pragma solidity ^0.7.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract LikeBlock
{
  uint public imageCount;
  uint public totalLikes;
  uint lastReward;
  address likeToken;
  address owner;
  mapping(address => mapping(uint => Image)) images;
  mapping(address => uint) donators;

  struct Image {
    uint likes;
    string IPFShash;
    string description;
  }

  constructor(address _likeToken) 
  {
    likeToken = _likeToken;
    owner = msg.sender;
  }

  function uploadImage(string memory _IPFShash, string memory _description) external 
  {
    require(bytes(_IPFShash).length <= 50, 'IPFShash length is too big');
    require(bytes(_description).length <= 100, 'Description length is too big');

    images[msg.sender][imageCount] = Image(0, _IPFShash, _description);
    imageCount++;
  }

  function likeImage() external
  {

    rewardDonators();
  }

  function rewardDonators() private 
  {
    if (lastReward <= block.timestamp - 86400) {
      //reward
      lastReward = block.timestamp;
    }
  }
}
